import React, { useState, useEffect } from "react";
import axios from "axios";
import ArticleDetails from "./ArticleDetails";
const API_KEY = "PbYA24xyseFrrcavsfTNUGBuYvpORMVR"; // Replace with your actual API key
const MOST_VIEWED_SECTION = "viewed";
const ArticleList = () => {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        `https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=${API_KEY}`
      );
      setArticles(response.data.results);
    };

    fetchData();
  }, []);

  const handleArticleClick = (article) => {
    setSelectedArticle(article);
  };

  return (
    <div className="container">
      <h2>Most Popular New York Times Articles</h2>
      <div className="button_wrapper">
        <button>1</button>
        <button>7</button>
        <button>30</button>
      </div>
      <ul className="article_list">
        {articles.map((article) => (
          <li key={article.uri} onClick={() => handleArticleClick(article)}>
            <h3> {article.title}</h3>

            {selectedArticle && (
              <div
                className={
                  selectedArticle.uri === article.uri && "article_details"
                }
              >
                {selectedArticle.uri === article.uri && (
                  <ArticleDetails article={selectedArticle} />
                )}
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ArticleList;
