import React, { useState, useEffect } from "react";
import ArticleList from "./ArticleList";
import axios from "axios";
import "./App.css";

const API_KEY = "YOUR_API_KEY";
const MOST_VIEWED_SECTION = "mostviewed";

function App() {
  const [articles, setArticles] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get(
          `https://api.nytimes.com/svc/mostpopular/v2/${MOST_VIEWED_SECTION}/all-sections/7.json?api-key=${API_KEY}`
        );
        setArticles(response.data.results);
      } catch (error) {
        setError(error);
      }
    };

    fetchArticles();
  }, []);

  return (
    <Router>
      <div className="App">
        <h1>New York Times Most Popular Articles</h1>
        {error && <p>Error fetching articles: {error.message}</p>}
        {articles.length > 0 && (
          <Routes>
            <Route path="/" element={<ArticleList articles={articles} />} />
            <Route
              path="/article/:id"
              element={<ArticleDetails articles={articles} />}
            />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
