import React, { useState, useEffect } from "react";
import axios from "axios";
import ArticleDetails from "./ArticleDetails";
const API_KEY = "YOUR_NY_TIMES_API_KEY"; // Replace with your actual API key

const ArticleList = () => {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [articleOpen, setArticleOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        `https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=PbYA24xyseFrrcavsfTNUGBuYvpORMVR`
      );
      setArticles(response.data.results);
    };

    fetchData();
  }, []);

  const handleArticleClick = (article) => {
    setSelectedArticle(article);
    if (articleOpen) {
      // Get reference to the div element (adjust selector as needed)
      const articleDetailsDiv = document.querySelector(".article_details");
      if (articleDetailsDiv) {
        articleDetailsDiv.classList.remove("article_details"); // Remove the class
        articleDetailsDiv.remove();
      }
    }
    setArticleOpen(true);
  };

  return (
    <div className="container">
      <h2>Most Popular New York Times Articles</h2>
      <ul className="article_list">
        {articles.map((article) => (
          <li key={article.uri} onClick={() => handleArticleClick(article)}>
            <h3> {article.title}</h3>

            {selectedArticle && (
              <div
                className={
                  selectedArticle.uri === article.uri && "article_details"
                }
              >
                {selectedArticle.uri === article.uri && (
                  <ArticleDetails article={selectedArticle} />
                )}
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ArticleList;
