import React, { useState, useEffect } from "react";
import axios from "axios";
import ArticleDetails from "./ArticleDetails";
const API_KEY = "YOUR_NY_TIMES_API_KEY"; // Replace with your actual API key

const ArticleList = () => {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        `https://api.nytimes.com/svc/mostpopular/v2/viewed/1.json?api-key=PbYA24xyseFrrcavsfTNUGBuYvpORMVR`
      );
      setArticles(response.data.results);
    };

    fetchData();
  }, []);

  const handleArticleClick = (article) => {
    setSelectedArticle(article);
    const currentClass = articleDetailsRef.current.className || ""; // Get current class
    articleDetailsRef.current.className = currentClass.includes("d-none")
      ? currentClass.replace("d-none", "") // Remove 'd-none' if present
      : `${currentClass} d-none`; // Add 'd-none' if absent
  };

  // Reference to the article_detail element
  const articleDetailsRef = React.createRef();

  return (
    <div className="container">
      <h2>Most Popular New York Times Articles</h2>
      <ul className="article_list">
        {articles.map((article) => (
          <li key={article.uri}>
            <a href={article.uri} onClick={() => handleArticleClick(article)}>
              {article.title}
            </a>

            {selectedArticle && <ArticleDetails article={selectedArticle} />}
          </li>
        ))}
      </ul>
      <div
        ref={articleDetailsRef}
        className={
          selectedArticle ? "article_details" : "d-none article_details"
        }
      >
        {/* ... content to show when an article is selected ... */}
        {selectedArticle && <ArticleDetails article={selectedArticle} />}
      </div>
    </div>
  );
};

export default ArticleList;
