import React, { useState, useEffect } from "react";
import ArticleList from "./ArticleList";
import axios from "axios";
import "./App.css";

const API_KEY = "PbYA24xyseFrrcavsfTNUGBuYvpORMVR";
const MOST_VIEWED_SECTION = "viewed";

function App() {
  const [articles, setArticles] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get(
          `https://api.nytimes.com/svc/mostpopular/v2/${MOST_VIEWED_SECTION}/all-sections/7.json?api-key=${API_KEY}`
        );
        setArticles(response.data.results);
      } catch (error) {
        setError(error);
      }
    };

    fetchArticles();
  }, []);

  return (
    <div className="App">
      <h1>New York Times Most Popular Articles</h1>
      {error && <p>Error fetching articles: {error.message}</p>}
      {articles.length > 0 && <ArticleList articles={articles} />}
    </div>
  );
}

export default App;
