import React, { useState, useEffect } from "react";
import axios from "axios";
import ArticleDetails from "./ArticleDetails";
const API_KEY = "YOUR_NY_TIMES_API_KEY"; // Replace with your actual API key

const ArticleList = () => {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        `https://api.nytimes.com/svc/mostpopular/v2/viewed/7.json?api-key=PbYA24xyseFrrcavsfTNUGBuYvpORMVR`
      );
      setArticles(response.data.results);
    };

    fetchData();
  }, []);

  const handleArticleClick = (article) => {
    setSelectedArticle(article);
  };

  return (
    <div className="container">
      <h2>Most Popular New York Times Articles</h2>
      <ul className="article_list">
        {articles.map((article) => (
          <li key={article.uri}>
            <a href={article.uri} onClick={() => handleArticleClick(article)}>
              {article.title}
            </a>
          </li>
        ))}
      </ul>
      <div ref={articleDetailsRef} className={selectedArticle ? "" : "d-none"}>
        {/* ... content to show when an article is selected ... */}
        {selectedArticle && (
          <div>
            <h2>{selectedArticle.title}</h2>
            <p>{selectedArticle.content}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ArticleList;
